    <table id="incomeTable">
      <thead>
        <tr>
          <th>Amount</th>
          <th>Category</th>
          <th>Notes</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody id="incomeItems">
        </tbody>
    </table>
  </div>

  <div id="Expense" class="tabcontent">
    <h2>Expense List</h2>
    <table id="expenseTable">
      <thead>
        <tr>
          <th>Amount</th>
          <th>Category</th>
          <th>Notes</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody id="expenseItems">
        </tbody>
