<script>
document.addEventListener("DOMContentLoaded", function() {
  var name = localStorage.getItem("user");
  if (name) {
    document.getElementById("welcomeMessage").innerText += " " + name;
  }

  const incomeList = JSON.parse(localStorage.getItem("incomeList")) || [];
  const incomeItems = document.getElementById("incomeItems");

  incomeList.forEach(income => {
    const listItem = document.createElement('li');
    listItem.textContent = `Amount: $${income.amount}, Type: ${income.type}, Notes: ${income.notes}`;
    incomeItems.appendChild(listItem);
  });

  const expenseList = JSON.parse(localStorage.getItem("expenseList")) || [];
  const expenseItems = document.getElementById("expenseItems");

  expenseList.forEach(expense => {
    const listItem = document.createElement('li');
    listItem.textContent = `Amount: $${expense.amount}, Type: ${expense.type}, Notes: ${expense.notes}`;
    expenseItems.appendChild(listItem);
  });

  document.getElementById("deleteButton").addEventListener("click", function() {
    var confirmation = confirm("Are you sure you want to delete this account?");
    if (confirmation) {
      localStorage.removeItem("user");
      window.location.href = "index.html";
    }
  });

  document.getElementById("addButton").addEventListener("click", function() {
    var dropdown = document.getElementById("addDropdown");
    dropdown.style.display = dropdown.style.display === "block" ? "none" : "block";
  });

  document.getElementById("incomeOption").addEventListener("click", function() {
    window.location.href = "income.html";
  });

  document.getElementById("expenseOption").addEventListener("click", function() {
    alert("Expense option selected!");
    // Add your functionality here
  });

  // Open the default tab
  document.querySelector('.tab button').click();
});

function openTab(evt, tabName) {
  var i, tabcontent, tablinks;
  tabcontent = document.getElementsByClassName("tabcontent");
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }
  tablinks = document.getElementsByClassName("tablinks");
  for (i = 0; i < tablinks.length; i++) {
    tablinks[i].className = tablinks[i].className.replace(" active", "");
  }
  document.getElementById(tabName).style.display = "block";
  evt.currentTarget.className += " active";
}

window.onclick = function(event) {
  if (!event.target.matches('#addButton')) {
    var dropdowns = document.getElementsByClassName("dropdown");
    for (var i = 0; i < dropdowns.length; i++) {
      var openDropdown = dropdowns[i];
      if (openDropdown.style.display === "block") {
        openDropdown.style.display = "none";
      }
    }
  }
}
</script>

</html>
