<script>
function addIncome() {
  const amount = document.getElementById('amount').value;
  const incomeType = document.getElementById('income-type').value;
  const notes = document.getElementById('notes').value;

  const incomeEntry = {
    amount: amount,
    type: incomeType,
    notes: notes
  };

  // Retrieve existing income list from localStorage
  const incomeList = JSON.parse(localStorage.getItem("incomeList")) || [];
  incomeList.push(incomeEntry);

  // Store updated income list back to localStorage
  localStorage.setItem("incomeList", JSON.stringify(incomeList));

  // Redirect to home.html
  window.location.href = "home.html";
}
</script>

